mapper.select:
SELECT meetListNum
FROM meetInfo
WHERE email = #{email}

SELECT * FROM gatchi WHERE meetListNum IN
#{item}
AND MEETCHECK = 1 ORDER BY meetListNum DESC

	SELECT * FROM (
    	SELECT * FROM gatchi WHERE meetCheck = 1 AND meetStatus = 1 ORDER BY DBMS_RANDOM.VALUE
	) WHERE ROWNUM <= ${count}

	SELECT * FROM (
